// src/componentes/TaskList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskList = () => {
    const [tasks, setTasks] = useState([]);

    useEffect(() => {
        const fetchTasks = async () => {
            try {
                const response = await axios.get('http://localhost/prueba/servidor/api.php');
                if (Array.isArray(response.data)) {
                    // Convertir los IDs a números enteros usando map
                    const tasksWithIntIds = response.data.map(task => ({
                        ...task,
                        id: parseInt(task.id) // Convertir id a número entero
                    }));
                    setTasks(tasksWithIntIds);
                } else {
                    console.error('Error fetching tasks: Response data is not an array');
                }
            } catch (error) {
                console.error('Error fetching tasks:', error);
            }
        };

        fetchTasks();
    }, []);

    return (
        <div className="container mt-4">
            <h2 className="mb-4">Lista de Tareas</h2>
            <table className="table">
                <thead className="thead-dark">
                    <tr>
                        <th scope="col">ID</th>
                        <th scope="col">Tarea</th>
                    </tr>
                </thead>
                <tbody>
                    {tasks.map(task => (
                        <tr key={task.id}>
                            <td>{task.id}</td>
                            <td>{task.task}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TaskList;
