import React, { useState, useEffect } from 'react';

function App() {
  const [nombres, setNombres] = useState([]);
  const [selectedNombre, setSelectedNombre] = useState('');

  useEffect(() => {
    const myHeaders = new Headers();
    myHeaders.append(
      'Authorization',
      'JWT eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjo0LCJ1c2VybmFtZSI6InBydWViYTIwMjJAY3VjLmVkdS5jbyIsImV4cCI6MTY0OTQ1MzA1NCwiY29ycmVvIjoicHJ1ZWJhMjAyMkBjdWMuZWR1LmNvIn0.MAoFJE2SBgHvp9BS9fyBmb2gZzD0BHGPiyKoAo_uYAQ'
    );

    const requestOptions = {
      method: 'GET',
      headers: myHeaders,
      redirect: 'follow'
    };

    fetch('http://consultas.cuc.edu.co/api/v1.0/recursos', requestOptions)
      .then(response => response.json())
      .then(result => {
        setNombres(result); // Assuming the API response is an array of objects
      })
      .catch(error => console.error(error));
  }, []);

  return (
    <div>
      <h1>Seleccione un Nombre</h1>
      <select value={selectedNombre} onChange={(e) => setSelectedNombre(e.target.value)}>
        <option value="">Seleccione un nombre</option>
        {nombres.map((nombre, index) => (
          <option key={index} value={nombre.nombre}>
            {nombre.nombre}
          </option>
        ))}
      </select>
    </div>
  );
}

export default App;
