            method: "GET",
            headers: myHeaders,
            redirect: "follow"
        };

        try {
            const response = await fetch("http://consultas.cuc.edu.co/api/v1.0/recursos", requestOptions);
            if (response.ok) {
                const data = await response.json();
                setRecursos(data);
