import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [tasks, setTasks] = useState([]);
    const [task, setTask] = useState('');

    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        try {
            const response = await fetch('http://localhost/prueba/servidor/api.php');
            const data = await response.json();
            setTasks(data);
        } catch (error) {
            console.error('Error fetching tasks:', error);
        }
    };

    const addTask = async () => {
        try {
            await fetch('http://localhost/prueba/servidor/api.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ task }),
            });
            setTask('');
            fetchTasks();
        } catch (error) {
            console.error('Error adding task:', error);
        }
    };

    const updateTask = async (id, newTask) => {
        try {
            await fetch('http://localhost/prueba/servidor/api.php', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ id, task: newTask }),
            });
            fetchTasks();
        } catch (error) {
            console.error('Error updating task:', error);
        }
    };

    const deleteTask = async (id) => {
        try {
            await fetch('http://localhost/prueba/servidor/api.php', {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ id }),
            });
            fetchTasks();
        } catch (error) {
            console.error('Error deleting task:', error);
        }
    };

    const toggleTaskCompletion = async (id, completed) => {
        try {
            await fetch('http://localhost/prueba/servidor/api.php', {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ id, completed: !completed }), // Invertir el estado de completado
            });
            fetchTasks();
        } catch (error) {
            console.error('Error toggling task completion:', error);
        }
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center">Lista de Tareas</h1>
            <div className="input-group mb-3">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Nueva Tarea"
                    value={task}
                    onChange={(e) => setTask(e.target.value)}
                />
                <button className="btn btn-primary" onClick={addTask}>
                    AÃ±adir
                </button>
            </div>
            <ul className="list-group">
                {tasks.map((task) => (
                    <li key={task.id} className="list-group-item d-flex justify-content-between align-items-center">
                        <input
                            type="text"
                            className={`form-control ${task.completed ? 'bg-success text-white' : ''}`}
                            style={{ backgroundColor: task.completed ? 'green' : '' }} // Cambia el color de fondo
                            value={task.task}
                            onChange={(e) => updateTask(task.id, e.target.value)}
                        />
                        <div>
                            <button
                                className={`btn ${task.completed ? 'btn-success' : 'btn-secondary'}`}
                                onClick={() => toggleTaskCompletion(task.id, task.completed)}
                            >
                                {task.completed ? 'Completado' : 'Marcar'}
                            </button>
                            <button className="btn btn-danger ms-2" onClick={() => deleteTask(task.id)}>
                                Eliminar
                            </button>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
