import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [tasks, setTasks] = useState([]);
    const [task, setTask] = useState('');

    useEffect(() => {
        fetchTasks();
    }, []);

    const fetchTasks = async () => {
        const response = await fetch('http://localhost/prueba/servidor/api.php');
        const data = await response.json();
        setTasks(data);
    };

    const addTask = async () => {
        await fetch('http://localhost/prueba/servidor/api.php', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ task }),
        });
        setTask('');
        fetchTasks();
    };

    const updateTask = async (id, newTask, completed = null) => {
        await fetch('http://localhost/prueba/servidor/api.php', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id, task: newTask, completed }),
        });
        fetchTasks();
    };

    const markTask = async (id, completed) => {
        await updateTask(id, null, completed);
    };

    const deleteTask = async (id) => {
        await fetch('http://localhost/prueba/servidor/api.php', {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id }),
        });
        fetchTasks();
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center">Lista de Tareas</h1>
            <div className="input-group mb-3">
                <input
                    type="text"
                    className="form-control"
                    placeholder="Nueva Tarea"
                    value={task}
                    onChange={(e) => setTask(e.target.value)}
                />
                <button className="btn btn-primary" onClick={addTask}>
                    AÃ±adir
                </button>
            </div>
            <ul className="list-group">
                {tasks.map((task) => (
                    <li key={task.id} className="list-group-item d-flex justify-content-between align-items-center">
                        <input
                            type="text"
                            className="form-control"
                            value={task.task}
                            onChange={(e) => updateTask(task.id, e.target.value)}
                            style={{ backgroundColor: task.completed ? 'lightgreen' : 'white' }}
                        />
                        <button className="btn btn-success" onClick={() => markTask(task.id, !task.completed)}>
                            {task.completed ? 'Desmarcar' : 'Marcar'}
                        </button>
                        <button className="btn btn-warning" onClick={() => updateTask(task.id, task.task)}>
                            Actualizar
                        </button>
                        <button className="btn btn-danger" onClick={() => deleteTask(task.id)}>
                            Eliminar
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
