import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const [tasks, setTasks] = useState([]);
    const [task, setTask] = useState('');
    const [editTask, setEditTask] = useState({ id: null, task: '' });
    const [recursos, setRecursos] = useState([]);

    useEffect(() => {
        fetchRecursos();
        fetchTasks();
    }, []);

    const fetchRecursos = async () => {
        const myHeaders = new Headers();
        myHeaders.append("Authorization", "JWT eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJ1c2VyX2lkIjo0LCJ1c2VybmFtZSI6InBydWViYTIwMjJAY3VjLmVkdS5jbyIsImV4cCI6MTY0OTQ1MzA1NCwiY29ycmVvIjoicHJ1ZWJhMjAyMkBjdWMuZWR1LmNvIn0.MAoFJE2SBgHvp9BS9fyBmb2gZzD0BHGPiyKoAo_uYAQ");

        const requestOptions = {
            method: "GET",
            headers: myHeaders,
            redirect: "follow"
        };

        try {
            const response = await fetch("http://consultas.cuc.edu.co/api/v1.0/recursos", requestOptions);
            if (response.ok) {
                const data = await response.json();
                console.log(data); // Verifica los datos obtenidos en la consola
                setRecursos(data);
            } else {
                console.error('Error al obtener los recursos');
            }
        } catch (error) {
            console.error('Error de red:', error);
        }
    };

    const fetchTasks = async () => {
        const response = await fetch('http://localhost/prueba/servidor/api.php');
        const data = await response.json();
        setTasks(data);
    };

    const addTask = async () => {
        await fetch('http://localhost/prueba/servidor/api.php', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ task }),
        });
        setTask('');
        fetchTasks();
    };

    const updateTask = async (id, newTask, isedit) => {
        await fetch('http://localhost/prueba/servidor/api.php', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id, task: newTask, isedit }),
        });
        fetchTasks();
        setEditTask({ id: null, task: '' });
    };

    const updateTaskCompletion = async (id, completed) => {
        const task = tasks.find(t => t.id === id);
        await fetch('http://localhost/prueba/servidor/api.php', {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id, task: task.task, completed: completed ? 1 : 0 }),
        });
        fetchTasks();
    };

    const deleteTask = async (id) => {
        await fetch('http://localhost/prueba/servidor/api.php', {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ id }),
        });
        fetchTasks();
    };

    const markTask = async (id) => {
        const task = tasks.find(t => t.id === id);
        await updateTaskCompletion(id, !task.completed);
    };

    const startEditTask = (task) => {
        setEditTask({ id: task.id, task: task.task });
    };

    const handleEditTaskChange = (e) => {
        setEditTask({ ...editTask, task: e.target.value });
    };

    const saveTask = async () => {
        if (editTask.id !== null) {
            await updateTask(editTask.id, editTask.task, 1);
        }
    };

    return (
        <div className="container mt-5">
            <h1 className="text-center">Lista de Tareas</h1>
            <div className="input-group mb-3">
                {/* Aquí mostramos los recursos en un select */}
                <select
                    className="form-control"
                    value={task}
                    onChange={(e) => setTask(e.target.value)}
                >
                    <option value="">Selecciona un recurso</option>
                    {recursos.map((recurso) => (
                        <option key={recurso.id} value={recurso.nombre}>{recurso.nombre}</option>
                    ))}
                </select>
                <div className="input-group-append">
                    <button className="btn btn-primary" type="button" onClick={addTask}>
                        Añadir
                    </button>
                </div>
            </div>
            <ul className="list-group">
                {tasks.map((task) => (
                    <li key={task.id} className="list-group-item d-flex justify-content-between align-items-center">
                        <div className="w-75">
                            <input
                                type="text"
                                className="form-control"
                                value={editTask.id === task.id ? editTask.task : task.task}
                                onChange={editTask.id === task.id ? handleEditTaskChange : null}
                                style={{ backgroundColor: task.completed ? 'green' : 'white' }}
                                disabled={editTask.id !== task.id}
                            />
                        </div>
                        <div className="btn-group" role="group" aria-label="Acciones">
                            {editTask.id === task.id ? (
                                <button className="btn btn-success mr-1" onClick={saveTask}>
                                    Guardar
                                </button>
                            ) : (
                                <>
                                    <button className="btn btn-warning mr-2" onClick={() => startEditTask(task)}>
                                        Editar
                                    </button>
                                    <button className="btn btn-success mr-2" onClick={() => markTask(task.id)}>
                                        Marcar
                                    </button>
                                    <button className="btn btn-danger mr-2" onClick={() => deleteTask(task.id)}>
                                        Eliminar
                                    </button>
                                </>
                            )}
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default App;
